// Calculates the result of the check
export default function calculateCheckResults(inResults, parameters) {
   const results = inResults;

   // Cache values for easy reference
   const extraSuccessOnCritical = parameters.extraSuccessOnCritical;
   const extraFailureOnCritical = parameters.extraFailureOnCritical;
   const difficulty = parameters.difficulty;
   results.criticalSuccesses = 0;
   results.criticalFailures = 0;
   results.successes = 0;
   results.extraSuccesses = 0;
   results.succeeded = false;

   // Calculate failures and successes
   for (let i = 0; i < results.dice.length; i++) {
      results.dice[i].success = false;
      // If this dice was a crit success
      if (results.dice[i].final === 6) {
         // Log the log the critical succcess
         results.criticalSuccesses += 1;
         results.successes += extraSuccessOnCritical ? 2 : 1;
         results.dice[i].success = true;
         results.dice[i].criticalSuccess = true;
      }

      // If this dice was a success
      else if (results.dice[i].final >= difficulty) {
         results.successes += 1;
         results.dice[i].success = true;
      }

      // If this dice was a critical failure
      else if (results.dice[i].final === 1) {
         results.criticalFailures += 1;
         results.dice[i].criticalFailure = true;
         if (extraFailureOnCritical) {
            results.successes -= 1;
         }
      }
   }

   // Calculate whether the check succeeded or not
   const complexity = parameters.complexity;
   if (complexity > 0) {
      // If succeeeded
      if (results.successes >= complexity) {
         results.succeeded = true;

         // If extra successes
         if (results.successes > complexity) {
            results.extraSuccesses = results.successes - complexity;
         }
      }
   }
   return results;
}

import calculateCheckResults from '~/check/CalculateCheckResults';
import calculateAttackCheckResults from '~/check/types/attack-check/CalculateAttackCheckResults';
import calculateCastingCheckResults from '~/check/types/casting-check/CalculateCastingCheckResults';
import calculateItemCheckResults from '~/check/types/item-check/CalculateItemCheckResults';
import calculateAttributeCheckResults from '~/check/types/attribute-check/CalculateAttributeCheckResults';
import calculateResistanceCheckResults from '~/check/types/resistance-check/CalculateResistanceCheckResults';

export default function recalculateCheckResults(check) {
   // Initialize results
   let results = {
      dice: check.results.dice,
      expertiseRemaining: check.results.expertiseRemaining
   };

   // Switch depending on check type
   switch (check.type) {
      case "attributeCheck": {
         return calculateAttributeCheckResults(results, check.parameters);
      }
      case "resistanceCheck": {
         return calculateResistanceCheckResults(results, check.parameters);
      }
      case "attackCheck": {
         return calculateAttackCheckResults(results, check.parameters);
      }
      case "castingCheck": {
         return calculateCastingCheckResults(results, check.parameters);
      }
      case "itemCheck": {
         return calculateItemCheckResults(results, check.parameters);
      }
      default: {
         return calculateCheckResults(results, check.parameters);
      }
   }
}

