import CharacterSheetInventoryAddItemDialog from '~/document/types/actor/types/character/sheet/tabs/inventory/CharacterSheetInventoryAddItemDialog';

export default class TitanCharacterSheet extends TitanActorSheet {
   addInventoryItem() {
      if (this.actor.isOwner) {
         const dialog = new CharacterSheetInventoryAddItemDialog(this.actor);
         dialog.render(true);
      }
      return;
   }

   // Delete Item from the sheet state
   deleteItem(itemId) {
      this.applicationState.deleteItem(itemId);

      return;
   }

   // Add the npc sheet class
   _getSheetClasses() {
      const retVal = super._getSheetClasses();
      retVal.push('titan-character-sheet');

      return retVal;
   }
}

import { writable } from 'svelte/store';

export default function createCharacterSheetState() {
   const { set, update, subscribe } = writable({
      scrollTop: {
         abilities: 0,
         actions: 0,
         inventory: 0,
         skills: 0,
         spells: 0
      },
      isExpanded: {
         actions: {},
         abilities: {},
         inventory: {},
         spells: {},
      },
      filter: {
         abilities: '',
         actions: '',
         inventory: '',
         skills: '',
         spells: ''
      },
      filterOptions: {
         abilities: {
            action: false,
            reaction: false,
            passive: false
         }
      },
      activeTab: 'skills'
   });

   // Remove an item
   function deleteItem(id) {
      update((state) => {
         if (state.isExpanded.actions[id]) {
            delete state.isExpanded.actions[id];
         }

         if (state.isExpanded.inventory[id]) {
            delete state.isExpanded.inventory[id];
         }

         if (state.isExpanded.spells[id]) {
            delete state.isExpanded.spells[id];
         }
         return state;
      });
   }

   return {
      set,
      update,
      subscribe,
      deleteItem
   };
}

