import { writable } from 'svelte/store';

export default function createNPCSheetState() {
   const { set, update, subscribe } = writable({
      scrollTop: {
         abilities: 0,
         inventory: 0,
         skills: 0,
         spells: 0,
         notes: 0
      },
      isExpanded: {
         abilities: {},
         inventory: {},
         spells: {},
      },
      filter: {
         abilities: '',
         inventory: '',
         skills: '',
         spells: ''
      },
      filterOptions: {
         inventory: {
            weapon: false,
            armor: false,
            shield: false,
            equipment: false,
            commodity: false,
         },
         abilities: {
            action: false,
            reaction: false,
            passive: false
         }
      },
      activeTab: 'skills'
   });

   // Remove an item
   function deleteItem(id) {
      update((state) => {
         if (state.isExpanded.inventory[id] === false || state.isExpanded.inventory[id] === true) {
            delete state.isExpanded.inventory[id];
         }

         if (state.isExpanded.abilities[id] === false || state.isExpanded.abilities[id] === true) {
            delete state.isExpanded.abilities[id];
         }

         if (state.isExpanded.spells[id] === false || state.isExpanded.spells[id] === true) {
            delete state.isExpanded.spells[id];
         }


         return state;
      });
   }

   return {
      set,
      update,
      subscribe,
      deleteItem
   };
}

import TitanCharacterComponent from '~/document/types/actor/types/character/Character.js';

export default class TitanPlayerComponent extends TitanCharacterComponent {

   setInitialData(initialData) {
      super.setInitialData(initialData);
      const prototypeToken = initialData.prototypeToken;
      prototypeToken.sight = {
         enabled: true
      };
      prototypeToken.actorLink = true;
      prototypeToken.disposition = CONST.TOKEN_DISPOSITIONS.FRIENDLY;

      return;
   }

   // Prepare Player type specific data
   prepareDerivedData() {
      super.prepareDerivedData();
      const xp = this.parent.system.xp;
      xp.available = xp.earned - this._getSpentXP();

      return;
   }

   _getAttributeXPCost(value) {
      // Attribute Cost starts at 2 for rank 2, and increases by consecutive odd integers, starting with 5
      let retVal = 0;
      let intervalCost = 2;
      let oddNumber = 5;
      for (let idx = 1; idx < value; idx++) {
         retVal += intervalCost;
         intervalCost = oddNumber;
         oddNumber += 2;
      }

      return retVal;
   }

   _getSkillXPCost(value) {
      // Skill Cost starts at 1 for rank 1, and increases by consecutive even integers, starting with 2
      let retVal = 0;
      let intervalCost = 1;
      let evenNumber = 2;
      for (let idx = 0; idx < value; idx++) {
         retVal += intervalCost;
         intervalCost = evenNumber;
         evenNumber += 2;
      }

      return retVal;
   }
}

