         abilities: '',
         inventory: '',
         skills: '',
         spells: ''
      },
      filterOptions: {
         inventory: {
            weapon: false,
            armor: false,
            shield: false,
            equipment: false,
            commodity: false,
         },
         abilities: {
            action: false,
            reaction: false,
            passive: false
         }
      },
      activeTab: 'skills'
   });

   // Remove an item
   function deleteItem(id) {
      update((state) => {
         if (state.isExpanded.inventory[id] === false || state.isExpanded.inventory[id] === true) {
            delete state.isExpanded.inventory[id];
         }

         if (state.isExpanded.abilities[id] === false || state.isExpanded.abilities[id] === true) {
