export default function shouldGetCheckOptions() {
   // If Get Check Options is true by default, return true if modifier is not pressed.
   // Otherwise, return false unless modifer is pressed.
   const retVal = game.settings.get('titan', 'getCheckOptions') === true;
   return isModifierActive() ? !retVal : retVal;
}

export default function sort(a, b) {
   if (a < b) {
      return -1;
   }
   if (a > b) {
      return 1;
   }
   return 0;
}

// Returns an object with arrays of the inputted array of objects
// mapped by the return value of a function accepting the object as an argument
export default function sortObjectsIntoContainerByFunctionValue(objects, sortFunction) {
   // Create retval object
   const retVal = {};

   // For each object
   for (const object of objects) {

      // Find the value of the function for this object
      const sortedKey = sortFunction(object);

      // If the array for the value of this object's key does not already exist in the retval,
      // then create the array and add it to the retval
      if (!retVal[sortedKey]) {
         retVal[sortedKey] = [];
      }

      // Add this object to the array for the value of this object's key
      retVal[sortedKey].push(object);
   }

   return retVal;
}

// Returns an object with arrays of the inputted array of objects
// mapped by the value of a respective key shared by each object
export default function sortObjectsIntoContainerByKeyValue(objects, key) {
   // Create retval object
   const retVal = {};

   // For each object
   for (const object of objects) {

      // Find the value of the key for this object
      const keyArray = object[key];

      // If the array for the value of this object's key does not already exist in the retval,
      // then create the array and add it to the retval
      if (!retVal[keyArray]) {
         retVal[keyArray] = [];
      }

      // Add this object to the array for the value of this object's key
      retVal[keyArray].push(object);
   }

   return retVal;
}

export default function warn(message) {
   console.warn(`TITAN | ${message}`);
   return;
}

